allow:
Bash(powershell.exe -Command "
Write-Host ''==========================================''
Write-Host ''📊 데이터베이스 현황''
Write-Host ''==========================================''
Write-Host ''''

# 테이블 존재 확인
$tables = @(''real_estates'', ''transactions'', ''chat_sessions'', ''chat_messages'')

foreach ($table in $tables) {
    try {
        $count = psql -U postgres -d real_estate -t -c ""SELECT COUNT(*) FROM $table;"" 2>$null
        if ($LASTEXITCODE -eq 0) {
            Write-Host ""✅ $table : $count 개""
        } else {
            Write-Host ""❌ $table : 테이블 없음""
        }
    } catch {
        Write-Host ""❌ $table : 테이블 없음""
    }
}

Write-Host ''''
Write-Host ''==========================================''
")
